parameters.jsCode:
// Build quotation variables for template processing
const quotation = $('Fetch Quotation Data').first().json;
const revenueItems = $('Fetch Revenue Items').all().map(item => item.json);
const template = $('Fetch Template').first().json;
const webhookData = $('Webhook Trigger').first().json;

// Format date helper
function formatDate(dateString) {
  if (!dateString) return '';
  try {
    const date = new Date(dateString);
    return date.toLocaleDateString('id-ID', {
      day: '2-digit',
      month: '2-digit',
      year: 'numeric'
    });
  } catch {
    return dateString;
  }
}

// Build items array with calculated amounts
const items = revenueItems.map(item => ({
  description: item.description || '',
  category: item.category || '',
  quantity: Number(item.quantity) || 1,
  unit: item.unit || 'unit',
  unit_price: Number(item.unit_price) || 0,
  amount: item.subtotal !== null 
    ? Number(item.subtotal) 
    : (Number(item.quantity) || 1) * (Number(item.unit_price) || 0)
}));

// Calculate total from items or use stored total_revenue
const totalAmount = quotation.total_revenue !== null && quotation.total_revenue !== undefined
  ? Number(quotation.total_revenue)
  : items.reduce((sum, item) => sum + item.amount, 0);

// Build scope from available fields
const scope = quotation.scope_of_work || quotation.description || quotation.title || '';

// Build terms from available fields
const terms = quotation.terms_conditions || 'Standard terms and conditions apply.';

// Build variables context
const variables = {
  quotation_number: quotation.quotation_number || '',
  quotation_date: formatDate(quotation.quotation_date || quotation.created_at),
  valid_until: formatDate(quotation.valid_until),
  customer_name: quotation.customers?.name || '',
  customer_address: quotation.customers?.address || '',
  customer_email: quotation.customers?.email || '',
  project_name: quotation.projects?.name || '',
  title: quotation.title || '',
  commodity: quotation.commodity || '',
  origin: quotation.origin || '',
  destination: quotation.destination || '',
  scope: scope,
  items: items,
  total_amount: totalAmount,
  terms: terms,
  notes: quotation.notes || ''
};

return {
  quotation_id: webhookData.quotation_id,
  user_id: webhookData.user_id,
  template: template,
  variables: variables,
  document_number: quotation.quotation_number
};

// Process template with variable substitution
const data = $input.first().json;
const template = data.template;
const variables = data.variables;

// Variable substitution function
function substituteVariable(template, varName, value) {
  const stringValue = value === null || value === undefined ? '' : String(value);
  const pattern = new RegExp('\\{\\{\\s*' + varName.replace(/[.*+?^${}()|[\]\\]/g, '\\$&') + '\\s*\\}\\}', 'g');
  return template.replace(pattern, stringValue);
}

// Process loops
function processLoop(template, loopName, items) {
  const escapedName = loopName.replace(/[.*+?^${}()|[\]\\]/g, '\\$&');
  const loopPattern = new RegExp(
    '\\{\\{#\\s*' + escapedName + '\\s*\\}\\}([\\s\\S]*?)\\{\\{/\\s*' + escapedName + '\\s*\\}\\}',
    'g'
  );

  return template.replace(loopPattern, (_, loopContent) => {
    if (!Array.isArray(items) || items.length === 0) return '';

    return items.map(item => {
      let rendered = loopContent;
      for (const [key, value] of Object.entries(item)) {
        rendered = substituteVariable(rendered, key, value);
      }
      return rendered;
    }).join('');
  });
}

// Start with HTML template
let html = template.html_template || '';

// Inject CSS styles
if (template.css_styles) {
  html = substituteVariable(html, 'styles', template.css_styles);
}

// Inject letterhead if enabled
const letterheadHtml = `
<div class="letterhead" style="text-align: center; margin-bottom: 20px;">
  <h2 style="margin: 0;">PT. Gama Intisamudera</h2>
  <p style="margin: 5px 0; font-size: 12px;">Heavy-Haul Logistics Solutions</p>
  <p style="margin: 5px 0; font-size: 11px;">Jl. Example Address No. 123, Jakarta, Indonesia</p>
  <p style="margin: 5px 0; font-size: 11px;">Tel: +62 21 1234567 | Email: info@gama-group.co</p>
</div>
`;

if (template.include_letterhead) {
  html = substituteVariable(html, 'letterhead', letterheadHtml);
} else {
  html = substituteVariable(html, 'letterhead', '');
}

// Process items loop
html = processLoop(html, 'items', variables.items);

// Substitute all other variables
for (const [key, value] of Object.entries(variables)) {
  if (key !== 'items') {
    html = substituteVariable(html, key, value);
  }
}

return {
  ...data,
  processed_html: html
};

// Prepare file for upload
const prevData = $('Process Template').first().json;
const pdfBinary = $input.first().binary;

const now = new Date();
const year = now.getFullYear();
const month = String(now.getMonth() + 1).padStart(2, '0');
const timestamp = now.getTime();

// Sanitize document number for filename
const sanitizedNumber = (prevData.document_number || 'document')
  .replace(/[^a-zA-Z0-9_-]/g, '_')
  .replace(/_+/g, '_');

const filename = `${sanitizedNumber}_${timestamp}.pdf`;
const storagePath = `quotation/${year}/${month}/${filename}`;

return {
  ...prevData,
  storage_path: storagePath,
  filename: filename,
  binary: pdfBinary
};

// Build file URL and prepare record data
const prevData = $('Prepare Upload').first().json;
const uploadResult = $input.first().json;

const supabaseUrl = $env.SUPABASE_URL;
const fileUrl = `${supabaseUrl}/storage/v1/object/public/generated-documents/${prevData.storage_path}`;

// Estimate file size (will be updated with actual size)
const fileSizeKb = Math.round((prevData.processed_html?.length || 0) * 2 / 1024);

return {
  template_id: prevData.template.id,
  document_type: 'quotation',
  document_number: prevData.document_number,
  entity_type: 'quotation',
  entity_id: prevData.quotation_id,
  file_url: fileUrl,
  file_name: prevData.filename,
  file_size_kb: fileSizeKb,
  generated_at: new Date().toISOString(),
  generated_by: prevData.user_id,
  variables_data: prevData.variables
};

